DROP TABLE IF EXISTS `course`;

create table course
(
    courseID             int auto_increment
        primary key,
    creator              int                                 not null comment 'memberID',
    eventName            varchar(50)                         not null,
    expectedPrice        int                                 not null,
    courseStartDate      date                                not null,
    courseStartTime      datetime                            not null,
    courseDuration       time                                null,
    registrationDeadline timestamp                           not null,
    courseCreationDate   timestamp default CURRENT_TIMESTAMP not null,
    maximumCapacity      int                                 not null,
    description          text                                not null,
    photo                varchar(255)                        not null,
    location             varchar(50)                         not null,
    city                 varchar(10)                         not null,
    district             varchar(10)                         not null,
    detailedAddress      varchar(100)                        not null,
    categoryID           varchar(10)                         not null,
    currentEnrolment     int       default 0                 null,
    courseStatus         int       default 1                 null comment '1.可參加 2. 暫停報名 3. 截止報名 4:滿團 5:強制下架',
    paidAdvertising      int       default 0                 null,
    paidAdvertisingTime  datetime                            null
);

